import React from "react";
import logo from "../assets/images/bpurplewhite.png";
import Button from "./Button";

const Header = () => {
  const toggleMobileMenu = () => {
    const menu = document.querySelector(".mobile-menu");
    menu.classList.toggle("hidden");
  };

  return (
    <div className="z-50 flex justify-evenly gap-x-4 px-2 items-center pt-4 fixed top-0 right-0 left-0 bg-purple h-16 md:h-20 2xl:h-20">
      <div>
        <img className="w-[90px] h-[25px]" src={logo} alt="logo" />
      </div>

      <div className="hidden min-w-[27rem] min-h-12 lg:flex justify-evenly items-center font-Graphik text-sm">
        <a href="#about" className="border-none"> 
          {/* First Menu Item*/}
        </a>
        <a href="#why-us" className="">
          {/* second Menu Item*/}
        </a>
        <a href="#stacked-covered" className="">
          {/*  Menu Item 3*/}
        </a>
        <a href="#faqs" className=""> 
          {/* First Menu Item 4 */}
        </a>
      </div>

      {/* large */}
      <div className="flex items-center">
        <div className="hidden lg:flex">
          <Button />
        </div>
      </div>

      <div className="md:hidden xl:hidden 2xl:hidden lg:hidden sm:hidden flex items-center bg-white">
        <button
          className="outline-none mobile-menu-button"
          onClick={toggleMobileMenu}
        >
          <svg
            className="w-9 h-6 text-gray-500 hover:text-green-500"
            fill="none"
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth="2"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path d="M4 6h16M4 12h16M4 18h16"></path>
          </svg>
        </button>
      </div>

      {/* mobile */}
      <div className="hidden md:block mobile-menu absolute top-16 md:top-20 2xl:top-20 w-full bg-gray">
        <ul className="flex flex-col items-center gap-y-2 text-center">
          <li>

            <a
              href="https://forms.gle/6jM1FNvHhT1VTpNV7"
              className="md:hidden px-2 py-4 hover:bg-green-500 transition duration-300 text-purple font-bold"
            >
              {/* First Menu Item*/}
            Enrol  </a>
          </li>
              {/* First Menu Item*/}



        </ul>
      </div> 
    </div>
  );
};

export default Header;
