            </div>
          </div>
          </div>

               {/* <div className="justify-right sm:p-2 xl:w-1/3 md:w-1/3 lg:w-1/3 sm:w-1/3 2xl:w-1/3">
                  <img 
                    className="h-[25rem] xl:h-[40rem] object-cover sm:mt-2 mt-5 xl:mt-5 rounded-lg h-25rem"
                    src={girl22}
                    alt="girl"
                 />
                </div>  */}


          </div>






    );
  };




export default Banner;
