import React from "react";
import Button from "./Button";

const Testimonials = () => {
  return (
    <div>
        <h3 className="py-2 pt-10 mt-5 font-semibold text-3xl pl-10 text-purple w-2/2 ">
        Testimonials
              </h3>
    <div className="flex flex-wrap justify-center mt-10 pt-10 mb-10 pb-10  xl:gap-x-4 gap-y-8 xl:mx-2 h-30">
        <div className="max-w-sm w-2/3 lg:max-w-full lg:flex gap-y-8 xl:mx-2">


        <div className="bg-white rounded-b lg:rounded-b-none lg:rounded-r p-4 flex flex-col justify-between leading-normal">
        <div className="mb-8">
        <div className="text-gray-900 font-bold text-xl mb-2">Ayotomiwa Abigail</div>
        <p className="text-gray-700 text-base">GIS is an effective instrument for collecting, processing, 
        and displaying spatial and geographic data. Numerous sectors, including urban planning, environmental management, and resource allocation, 
        can use this technology. Organizations can use GIS to visualize data in maps, 
        graphs, and tables, analyze patterns, and uncover connections that might not be obvious through conventional data analysis.</p>
        </div>

        </div>
        <div className="h-60 lg:h-auto lg:w-80 flex-none border  bg-contain rounded-t lg:rounded-t-none lg:rounded-l text-center overflow-hidden bg-hero2">
            </div>
        </div>



  </div>
  </div>

  );
};

export default Testimonials;
