import React from "react";


const Timer = () => {

    function beer() {
        return {
            seconds: '00',
            minutes: '00',
            hours: '00',
            days: '00',
            distance: 0,
            countdown: null,
            beerTime: new Date('May 17, 2021 00:00:00').getTime(),
            now: new Date().getTime(),
            start: function() {
                this.countdown = setInterval(() => {
                    // Calculate time
                    this.now = new Date().getTime();
                    this.distance = this.beerTime - this.now;
                    // Set Times
                    this.days = this.padNum( Math.floor(this.distance / (1000*60*60*24)) );
                    this.hours = this.padNum( Math.floor((this.distance % (1000*60*60*24)) / (1000*60*60)) );
                    this.minutes = this.padNum( Math.floor((this.distance % (1000*60*60)) / (1000*60)) );
                    this.seconds = this.padNum( Math.floor((this.distance % (1000*60)) / 1000) );
                    // Stop
                    if (this.distance < 0) {
                        clearInterval(this.countdown);
                        this.days = '00';
                        this.hours = '00';
                        this.minutes = '00';
                        this.seconds = '00';
                    }
                },100);
            },
            padNum: function(num) {
                var zero = '';
                for (var i = 0; i < 2; i++) {
                    zero += '0';
                }
                return (zero + num).slice(-2);
            }
        }
    }




    return (

        <div>
        <div class=" bg-yellow-500 flex justify-center px-5 py-5" >
            <div class="text-yellow-100">
                <h1 class="text-3xl text-center mb-3 font-extralight text-white ">Counting Down</h1>
                <div class="text-6xl text-center flex w-full items-center justify-center">
                    <div class="text-2xl mr-1 text-white font-extralight">in</div>
                    <div class="w-24 mx-1 p-2 bg-white text-yellow-500 rounded-lg">
                        <div class="font-mono leading-none" x-text="days">45</div>
                        <div class="font-mono uppercase text-sm leading-none">Days</div>
                    </div>
                    <div class="w-24 mx-1 p-2 bg-white text-yellow-500 rounded-lg">
                        <div class="font-mono leading-none" x-text="hours">32</div>
                        <div class="font-mono uppercase text-sm leading-none">Hours</div>
                    </div>
                    <div class="w-24 mx-1 p-2 bg-white text-yellow-500 rounded-lg">
                        <div class="font-mono leading-none" x-text="minutes">05</div>
                        <div class="font-mono uppercase text-sm leading-none">Minutes</div>
                    </div>
                    <div class="text-2xl mx-1 text-white font-extralight">and</div>
                    <div class="w-24 mx-1 p-2 bg-white text-yellow-500 rounded-lg">
                        <div class="font-mono leading-none" x-text="seconds">11</div>
                        <div class="font-mono uppercase text-sm leading-none">Seconds</div>
                    </div>
                </div>


           </div>
        </div>

        </div>


      );
   };     

export default Timer;
