import React from "react";
import Button from "./Button";

const Testimonials = () => {
  return (
    <section className="h-[80vh]">
        <h3 className="py-2 pt-10 mt-5 sm:mt-1 md:mt-1 font-semibold text-3xl pl-10 text-purple w-2/2 ">
        Testimonials
              </h3>
    <div className="flex flex-wrap justify-center mt-5 pt-5 mb-10 pb-10 xl:gap-x-4 gap-y-8 xl:mx-2 h-30">
        <div className="max-w-sm md:w-2/3 md:mtlg:max-w-full lg:flex gap-y-8 xl:mx-2">


        <div className="bg-transparent rounded-b lg:rounded-b-none lg:rounded-r p-4 sm:p-1 flex flex-col justify-between leading-normal">
        <div className="mb-8">

        <p className="text-gray-700 text-bold text-xl">I decided to join the virtual assistant path in the free tech bootcamp organised by bpurpleHQ 
        as a a newbiew, it was so engaging and while at it, I did not only learn the skill, 
        I was subjected to learning, leadership and collaboration skills.</p>
        </div>
        <div className="text-gray-900 font-bold text-xl mb-2">Olabisi Balogun <span className="text-sm font-light"> - Intern at bpurpleHQ</span></div>

        </div>
        <div className="h-60 lg:h-auto lg:w-80 flex-none bg-contain rounded-t lg:rounded-t-none lg:rounded-l text-center overflow-hidden bg-hero2">
            </div>
        </div>



  </div>
  </section>

  );
};

export default Testimonials;
