import React from "react";
import Button from "./Button";
import sf from "../assets/images/hero-2.png";
import po from "../assets/images/PO.jpeg";

const Testimonials = () => {
  return (
    <section className="">
        <h3 className="py-2 md:pt-10 mt-5 sm:mt-1 md:mt-1 font-sans font-semibold text-4xl text-center xl pl-10 text-purple w-2/2 ">
        Chief Host
              </h3>
    <div className="flex flex-wrap justify-center mt-5 pt-2 mb-10 pb-5 xl:gap-x-4 gap-y-8 xl:mx-2 h-30 font-sans">
        <div className="max-w-sm md:w-2/3 lg:max-w-full lg:flex gap-y-8 xl:mx-2">
        <div className="bg-transparent rounded-b lg:rounded-b-none lg:rounded-r p-4 sm:p-1 flex flex-col justify-between leading-normal">
        <div className="mb-8">
        <div className="text-gray-900 font-sans font-bold text-xl mb-5 ml-5">Dr David Ogbueli <span className="text-sm font-sans font-light"> - Founder, Golden Heart Foundation</span></div>
        <p className=" p-5 text-2xl font-sans font-light">Special thanks to the organisers of the bpurple bootcamp, I decided to join the virtual assistant path in the free tech bootcamp organised by bpurpleHQ 
        as a newbiew, it was so engaging and while at it, I did not only learn the skill, 
        I was subjected to learning, leadership and collaboration skills, it was really a great time, I had to opportunity
        to network with other candidates and my mentors.</p>
        </div>


        </div>

        {<img src={sf} alt="Card 1" className="w-full h-60 lg:h-auto lg:w-80 flex-none bg-cover rounded-t m-2 lg:rounded-t-none lg:rounded-l text-center overflow-hidden object-cover transition ease-in-out delay-150 hover:-translate-y-1 hover:scale-110 hover:grayscale duration-300 " />}
            </div>
        </div>


  {/* Speaker 2 */}

        <h3 className="bg-yllow py-2 md:pt-10 mt-5 sm:mt-1 md:mt-1 font-sans font-semibold text-4xl text-center xl pl-10 text-purple w-2/2 ">
        Chief Host
              </h3>
    <div className="flex flex-wrap justify-center mt-5 pt-2 mb-10 pb-5 xl:gap-x-4 gap-y-8 xl:mx-2 h-30 font-sans">
        <div className="max-w-sm md:w-2/3 lg:max-w-full lg:flex gap-y-8 xl:mx-2">
        <div className="bg-transparent rounded-b lg:rounded-b-none lg:rounded-r p-4 sm:p-1 flex flex-col justify-between leading-normal">
        <div className="mb-8">
        <div className="text-gray-900 font-sans font-bold text-xl mb-5 ml-5">Dr David Ogbueli <span className="text-sm font-sans font-light"> - Founder, Golden Heart Foundation</span></div>
        <p className=" p-5 text-2xl font-sans font-light">Special thanks to the organisers of the bpurple bootcamp, I decided to join the virtual assistant path in the free tech bootcamp organised by bpurpleHQ 
        as a newbiew, it was so engaging and while at it, I did not only learn the skill, 
        I was subjected to learning, leadership and collaboration skills, it was really a great time, I had to opportunity
        to network with other candidates and my mentors.</p>
        </div>

        </div>

        {<img src={po} alt="Card 1" className="w-full h-60 lg:h-auto lg:w-80 flex-none bg-cover rounded-t m-2 lg:rounded-t-none lg:rounded-l text-center overflow-hidden object-cover transition ease-in-out delay-150 hover:-translate-y-1 hover:scale-110 hover:grayscale duration-300 " />}
            </div>
        </div>





  </section>

  );
};

export default Testimonials;
